import{_ as e,c as t,o,J as r}from"./chunks/framework.CGIupE7Q.js";const n="/img-openwrt-wifi-setting/img0.png",a="/img-openwrt-wifi-setting/img1.png",p="/img-openwrt-wifi-setting/img2.png",s="/img-openwrt-wifi-setting/img3.png",c="/img-openwrt-wifi-setting/img4.png",d="/img-openwrt-wifi-setting/img5.png",m="/img-openwrt-wifi-setting/img6.png",g="/img-openwrt-wifi-setting/img7.png",w="/img-openwrt-wifi-setting/img8.png",f="/img-openwrt-wifi-setting/img9.png",P=JSON.parse('{"title":"OpenWrt 配置 WiFi 中继","description":"OpenWrt 连接其他 WiFi，然后这个路由器再自己发射出一个 WiFi 信号","frontmatter":{"title":"OpenWrt 配置 WiFi 中继","description":"OpenWrt 连接其他 WiFi，然后这个路由器再自己发射出一个 WiFi 信号","createdTime":"2025/1/14 15:49:34","updatedTime":"2025/7/13 22:23:30","readingTime":2,"category":["知识分享"],"tag":["OpenWrt"],"hash":"09f7c6727b9eaa33747c359664bfe35da7cfbc0b1639d67c8cefd85b3b6ef43d","head":[["meta",{"name":"robots","content":"index, nofollow"},""]]},"headers":[],"relativePath":"posts/openwrt-wifi-setting.md","filePath":"posts/openwrt-wifi-setting.md","lastUpdated":1760587086000}'),l={name:"posts/openwrt-wifi-setting.md"};function _(b,i,h,W,S,u){return o(),t("div",null,i[0]||(i[0]=[r('<h2 id="openwrt-配置-wifi-中继" tabindex="-1">OpenWrt 配置 WiFi 中继 <a class="header-anchor" href="#openwrt-配置-wifi-中继" aria-label="Permalink to &quot;OpenWrt 配置 WiFi 中继&quot;">​</a></h2><h3 id="连接需要中继的-wifi" tabindex="-1">连接需要中继的 wifi <a class="header-anchor" href="#连接需要中继的-wifi" aria-label="Permalink to &quot;连接需要中继的 wifi&quot;">​</a></h3><p>去到<code>Network -&gt; Wireless</code>，点击 <code>Scan</code><img src="'+n+'" alt=""></p><p>选择需要中继的 WiFi 网络，点击加入网络 <img src="'+a+'" alt=""></p><p><code>WPA passphrase</code>意思是要输入 wifi 密码<br><code>Lock to BSSID</code>表示这个网络与 MAC 地址绑定，不用担心 wifi 重名问题<br> 完成之后点击<code>Submit</code><img src="'+p+'" alt=""></p><p>这里直接默认即可，点击<code>Save</code><img src="'+s+'" alt=""></p><p>点击<code>Save &amp; Apply</code>，此时你的路由器已经连上了要中继的 wifi，并且电脑也可以上网了 <img src="'+c+'" alt=""></p><h3 id="开启当前路由器的-wifi" tabindex="-1">开启当前路由器的 wifi <a class="header-anchor" href="#开启当前路由器的-wifi" aria-label="Permalink to &quot;开启当前路由器的 wifi&quot;">​</a></h3><p>点击<code>add</code><br><img src="'+d+'" alt=""></p><p>配置模式为<code>Access Point</code>，即网络接入点，或者是说当前路由器的 wifi，怎么样叫它都可以<br><code>ESSID</code>也就是 wifi 名字<br><code>Network</code>表示当前配置属于哪个网络，选择 lan 网络，意思类似于，一台设备连上了这个 wifi 之后就相当于这个设备接到了这个路由器的 lan 口<br> 点击<code>Save</code><br><img src="'+m+'" alt=""></p><p>接着来到<code>Wireless Security</code>，加密方式可以选择这个，设置一下 wifi 密码，点击<code>Save</code><img src="'+g+'" alt=""></p><p>点击<code>Save &amp; Apply</code><br><img src="'+w+'" alt=""></p><p>此时可以看到当前路由器的 wifi<br><img src="'+f+'" alt=""></p>',13)]))}const T=e(l,[["render",_]]);export{P as __pageData,T as default};
